public class PrintEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("10"+"10");	//문자열 + 문자열 -> 문자열
		System.out.println("10"+123);	//문자열 + 숫자 -> 숫자가 문자열의 형태로 결합
		System.out.println(10+123);		//숫자 + 숫자 -> 숫자
		System.out.println(1+"+"+2+"="+(1+2));	//숫자 + 문자열 + 숫자 + 문자열 + 숫자
	}

}
